const { encodeToOC8Png, decodeFromOC8Png, crc8 } = require('../src/oc8');
const fs = require('fs');

if (require.main === module) {
  const assert = require('assert');
  const payload = Buffer.from('hello nezlephant');
  const png = encodeToOC8Png(payload);
  const out = decodeFromOC8Png(png);
  assert.strictEqual(out.toString('utf8'), 'hello nezlephant');
  console.log('roundtrip ok');
}

module.exports = {};
