type HttpMethod = "GET" | "POST" | "PUT" | "PATCH" | "DELETE";
type RouteDef = {
    method: HttpMethod;
    path: string;
    name?: string;
};
type RouteTable = RouteDef[];

declare function getRoutes(): RouteTable;
declare function findRoute(name: string): RouteDef | undefined;
/** Génère un path en remplaçant les params :id */
declare function url(name: string, params?: Record<string, string | number>): string;

export { findRoute, getRoutes, url };
