// helpers for encrypt/decrypt using nezlephant integration (placeholder)
export async function encryptToImage(inputPath, outPath, key) {
    // placeholder: delegate to @funeste38/envapt-nezlephant
    const mod = await import('@funeste38/envapt-nezlephant').catch(() => null);
    if (!mod)
        throw new Error('Optional dependency @funeste38/envapt-nezlephant not installed');
    return mod.encryptFileToImage(inputPath, outPath, key);
}
export async function decryptFromImage(imagePath, outPath, key) {
    const mod = await import('@funeste38/envapt-nezlephant').catch(() => null);
    if (!mod)
        throw new Error('Optional dependency @funeste38/envapt-nezlephant not installed');
    return mod.decryptImageToFile(imagePath, outPath, key);
}
